import { useEffect, useState } from "react";
import ShopDetail from "./ShopDetail";
import { useParams } from "react-router-dom";

let arrayFilter = null

const ShopList= () => {

    const [startproductos, setStartProductos] = useState([]);
    const {categoryid} = useParams();
   

    useEffect(() => {
        getStartData()
        if (categoryid !== undefined) {
                arrayFilter = startproductos.filter(producto => producto.Categoria === categoryid)
        
        }
    }
    , []);

    const getStartData = () => {
        const URL = '../../json/api.json'
            fetch(URL)
                .then(res => res.json())        
                .then(data => setStartProductos(data))
        }
  return (
      <div>
                <div className="flex flex-wrap justify-center">
                (arrayFilter) ? arrayFilter.map(p => <ShopDetail key={p.id} producto={p} />) : {startproductos.map(p => <ShopDetail key={p.id} producto={p}/>)}
            </div>
       </div>
  )
}
export default ShopList
