import { useState } from "react";

const Contador = ({stock, cantidad , bottonActivo ,onAdd}) => {

    let inicial = 0;
    cantidad > 0 ? inicial = cantidad : inicial = 0;

    const [count, setCount] =  useState(inicial);

    const addHandler = () => {
        count >= stock ? setCount(count) : setCount(count + 1)
    }

    const restHandler = () => {
    count >= 1 ? setCount(count - 1) : setCount(0)
    }


    return (
    <>  
        <button onClick={restHandler} className='btn btn-accent btn-xs'> - </button>
        <strong>{count}</strong>
        <button  onClick={addHandler} className='btn btn-primary btn-xs'> + </button>
        <div className="card-actions justify-endd">
        { bottonActivo ? <button onClick ={()=> onAdd(count)} className='btn btn-primary btn-xs'> Agregar a Carrito </button> : null}
        </div>
    </>
    )
}
export default Contador
