import { useState } from "react";

const Contador = () => {

    let [count, setCount] = useState(0);

    const addHandler = () => {
        setCount(count ++)
    }

    const restHandler = () => {
        setCount(count - 1)

    }

    return (
    <>  
        <div>Contador</div>
        <button onClick={restHandler}>-</button>
        <strong>{count}</strong>
        <button onClick={addHandler}>+</button>
    </>
    )
}
export default Contador
