import { Link } from "react-router-dom";
import useCartContext from "../../store/CartContext";
import { createBuyOrder} from "../../data/index";

function CartView() {

    const {cart, removeFromCart, clearCart, calcPriceCart} = useCartContext();

function handleBuy(){
  const itemsToBuy = cart.map((item) => (
    { title: item.title,
      price: item.precio,
      cant: item.cant,
      id: item.id,
      }
    ))
    const buyOrder = {
    buyer:{
      name: "Carlos",
      email: "carlos@shoop.com",
      phone: "5493415839183",
    },

    items: itemsToBuy,
    total: calcPriceCart(),
  }

  createBuyOrder(buyOrder)

}



if (cart.length === 0){
  return <div>
    <h4>No hay items en en el carrito</h4>
    <Link to='/'> Volver para comprar</Link>
      </div>

}else{
    return /* {  <div>
              {cart.map(itemCart => {
                      return <div key={itemCart.id}>


                      
                          <h4>{itemCart.title}</h4>
                          <p>{itemCart.description}</p>
                          <p>{itemCart.precio}</p>
                          <p>{itemCart.cant}</p>
                          <button onClick ={() => removeFromCart(itemCart.id)} style={{color: 'red'}} >Eliminar</button>
                          </div>
                })}
        <button onClick={clearCart} >Vaciar Carrito</button> <br/>
        <button onClick={handleBuy} > Comprar!!!</button>
        </div> }*/


        {  <div>
              {cart.map(itemCart => {
                      return <div key={itemCart.id}>
                        <div className="flex items-center hover:bg-gray-100 -mx-8 px-6 py-5">
            <div className="flex w-2/5"> 
              <div className="w-20">
                <img className="h-24" src={itemCart.thumbnailUrl} alt="" />
              </div>
              <div key= {itemCart.id}>
              <div className="flex flex-col justify-between ml-4 flex-grow">
                <span className="font-bold text-sm">{itemCart.title}</span>
                <span className="text-red-500 text-xs">{itemCart.description}</span>
                <button onClick ={() => removeFromCart(itemCart.id)} className="font-semibold hover:text-red-500 text-gray-500 text-xs" >Remove</button>
                          </div>
              </div>
            </div>
            <div className="flex justify-center w-1/5">
              <svg className="fill-current text-gray-600 w-3" viewBox="0 0 448 512"><path d="M416 208H32c-17.67 0-32 14.33-32 32v32c0 17.67 14.33 32 32 32h384c17.67 0 32-14.33 32-32v-32c0-17.67-14.33-32-32-32z"/>
              </svg>
  
              <input className="mx-2 border text-center w-8" type="text" value={itemCart.cant} />
  
              <svg className="fill-current text-gray-600 w-3" viewBox="0 0 448 512">
                <path d="M416 208H272V64c0-17.67-14.33-32-32-32h-32c-17.67 0-32 14.33-32 32v144H32c-17.67 0-32 14.33-32 32v32c0 17.67 14.33 32 32 32h144v144c0 17.67 14.33 32 32 32h32c17.67 0 32-14.33 32-32V304h144c17.67 0 32-14.33 32-32v-32c0-17.67-14.33-32-32-32z"/>
              </svg>
            </div>
            <span className="text-center w-1/5 font-semibold text-sm">{itemCart.precio}</span>
            <span className="text-center w-1/5 font-semibold text-sm">$400.00</span>
          </div>

                      
                          <h4>{itemCart.title}</h4>
                          <p>{itemCart.description}</p>
                          <p>{itemCart.precio}</p>
                          <p>{itemCart.cant}</p>
                          <button onClick ={() => removeFromCart(itemCart.id)} style={{color: 'red'}} >Eliminar</button>
                          </div>
                })}
        <button onClick={clearCart} >Vaciar Carrito</button> <br/>
        <button onClick={handleBuy} > Comprar!!!</button>
        </div> }

              }
            }
export default CartView;
