import { Link } from "react-router-dom";
import useCartContext from "../../store/CartContext";
import { createBuyOrder} from "../../data/index";
import Contador from "./ItemCount";

function CartView() {

    const {cart, removeFromCart, clearCart, calcPriceCart , agregaItems} = useCartContext();

function handleBuy(){
  const itemsToBuy = cart.map((item) => (
    { title: item.title,
      price: item.precio,
      cant: item.cant,
      id: item.id,
      }
    ))
    const buyOrder = {
    buyer:{
      name: "Carlos",
      email: "carlos@shop.com",
      phone: "5493415839183",
    },

    items: itemsToBuy,
    total: calcPriceCart(),
  }

  createBuyOrder(buyOrder)
}

function Agrega(id ){
    const cart2 = cart.map((item) => {
      if(item.id === id){
        item.cant = item.cant + 1;
      }
      cart = cart2;
      return cart;
    })
    
  }
  
function Resta(id ){
  const cart2 = cart.map((item) => {
    if(item.id === id){
      item.cant = item.cant - 1;
    }
    cart = cart2;
    return cart;
  })
  
}


if (cart.length === 0){
  return <div>
    <h4>No hay items en en el carrito</h4>
    <Link to='/'> Volver para comprar</Link>
      </div>

}else{
    return <div>
    <div className="bg-gray-100">
    <div className="container mx-auto mt-10">
      <div className="flex shadow-md my-10">
        <div className="w-3/4 bg-white px-10 py-10">
          <div className="flex justify-between border-b pb-8">
            <h1 className="font-semibold text-2xl">Shopping Cart</h1>
            <h2 className="font-semibold text-2xl">{cart.length} Items</h2>
          </div>
          <div className="flex mt-10 mb-5">
            <h3 className="font-semibold text-gray-600 text-xs uppercase w-2/5">Product Details</h3>
            <h3 className="font-semibold text-center text-gray-600 text-xs uppercase w-1/5 text-center">Quantity</h3>
            <h3 className="font-semibold text-center text-gray-600 text-xs uppercase w-1/5 text-center">Price</h3>
            <h3 className="font-semibold text-center text-gray-600 text-xs uppercase w-1/5 text-center">Total</h3>
          </div>
          
              {cart.map(itemCart => {
                      return <div key={itemCart.id}>
<div className="flex items-center hover:bg-gray-100 -mx-8 px-6 py-5">
            <div className="flex w-2/5">
              <div className="w-20">
                <img className="h-24" src={itemCart.thumbnailUrl} alt={itemCart.title} />
              </div>
              <div className="flex flex-col justify-between ml-4 flex-grow">
                <span className="font-bold text-sm">{itemCart.title}</span>
                <span className="text-red-500 text-xs">{itemCart.description}</span>
                <button onClick ={() => removeFromCart(itemCart.id)} className="font-semibold hover:text-red-500 text-gray-500 text-xs" >Eliminar</button>

              </div>
            </div>
            <div className="flex justify-center w-1/5">
    
              <button   className='btn btn-accent btn-xs'> - </button>
              <strong>{itemCart.cant}</strong>
              <button onClick={agregaItems}  className='btn btn-primary btn-xs'> + </button>
            </div>
            <span className="text-center w-1/5 font-semibold text-sm">{itemCart.precio}</span>
            <span className="text-center w-1/5 font-semibold text-sm"> ` { calcPriceCart()} `</span>
          </div>
        </div>
            })}
        <button onClick={clearCart} >Vaciar Carrito</button> <br/>
        <button onClick={handleBuy} > Comprar!!!</button>
        </div>

        <div id="summary" class="w-1/4 px-8 py-10">
          <h1 class="font-semibold text-2xl border-b pb-8">Order Summary</h1>
          <div class="flex justify-between mt-10 mb-5">
            <span class="font-semibold text-sm uppercase">Items {cart.length}</span>
            <span class="font-semibold text-sm">590$</span>
          </div>
          <div>
            <label class="font-medium inline-block mb-3 text-sm uppercase">Shipping</label>
            <select class="block p-2 text-gray-600 w-full text-sm">
              <option>Standard shipping - $10.00</option>
            </select>
          </div>
          <div class="py-10">
            <label for="promo" class="font-semibold inline-block mb-3 text-sm uppercase">Promo Code</label>
            <input type="text" id="promo" placeholder="Enter your code" class="p-2 text-sm w-full" />
          </div>
          <button class="bg-red-500 hover:bg-red-600 px-5 py-2 text-sm text-white uppercase">Apply</button>
          <div class="border-t mt-8">
            <div class="flex font-semibold justify-between py-6 text-sm uppercase">
              <span>Total cost</span>
              <span>$600</span>
            </div>
            <button class="bg-indigo-500 font-semibold hover:bg-indigo-600 py-3 text-sm text-white uppercase w-full">Checkout</button>
          </div>
        </div>


        </div>
        </div>
        </div>
        </div>
      
  }
}
export default CartView
