import { useState } from "react"

const contador = () => {
    const [count, setCount] = useState(0)
    console.log('aqui hay un render del componente condatador')

    const addHandler = () => {
        setCount(count + 1)
    }
    return (
    <>  
    <div>contador</div>
    <button>-</button>
    <strong>{count}</strong>
    <button onClick={addHandler}>+</button>


    </>
    )
    
}
export default contador
